sn_aia_tool.script:
(function(inputs) {
    // only string inputs are allowed 
    // return outputs object where the keys in it are understandable by LLM
    if (!inputs) return {
        message: 'No inputs found, could not make Knowledge updates.'
    };
    var gr = new GlideRecord('kb_knowledge');
    gr.addQuery('number', inputs.number);
    gr.orderByDesc('version');
    gr.setLimit(1);
    gr.query();
    if (gr.next()) {
        gr.text = inputs.article_body;
        if (inputs.article_body != '') {
            var updateStatus = gr.update();
            if (updateStatus) {
                return;
            }
            else {
                return {
                    message: 'Knowledge updates could not be made, update failed.'
                };
            }
        }
        else {
            return {
                message: 'No Knowledge updates available.'
            };
        }
    } else {
        return {
            message: 'No Knowledge article found, could not update.'
        };
    }
})(inputs);
