sn_aia_tool.script:
(function(inputs) {
    // only string inputs are allowed 
    // return outputs object where the keys in it are understandable by LLM  
    var gr = new GlideRecord('change_request');
    gr.addQuery('number', inputs.number);
    gr.query();
    if (gr.next()) {
        gr.implementation_plan = inputs.implementation_plan;
        gr.work_notes = inputs.implementation_plan; // Copy Implementation Plan to Work Notes too
        gr.update();
    }
})(inputs);
